import StatusBar from 'SourceComponent/StatusBar/StatusBar';
import { Checkout as SourceCheckout } from 'SourceRoute/Checkout/Checkout.component';
import {
    BILLING_STEP,
    DETAILS_STEP,
    SHIPPING_STEP
} from 'SourceRoute/Checkout/Checkout.config';

/* eslint-disable */
export class CheckoutComponent extends SourceCheckout {

    statusList = [
        {
          name: "shipping",
          active: false,
          finish: false,
          last: false,
          title: SHIPPING_STEP
        },
        {
          name: "billing",
          active: false,
          finish: false,
          last: false,
          title: BILLING_STEP
        },
        {
          name: "sucess",
          active: false,
          finish: false,
          last: true,
          title: DETAILS_STEP
        },
      ]

  renderProgressBar() {
      const { checkoutStep } = this.props;


      statusList.forEach((i, index) => {
          if(i.title === checkoutStep){
            i.active = true
            if(statusList[index-1]){
                statusList[index-1].finish = true
            }
          }
      })
    return <StatusBar info={statusList} fillColor="#F26322"/>;
  }
}

export default CheckoutComponent;
